def read_file(filename):
    result = []
    with open(filename, 'r') as file:
        for tmp in file.readlines():
            result.append(tmp.replace('\n', ''))

    return result


def write_to_file(filename, text):
    with open(filename, 'a') as file:
        file.write(f'{text}\n')


def bridge(private_key):
    address = web3.eth.account.from_key(private_key).address
    try:
        nft_id = nft_contract.functions.tokensOfOwner(address).call()[0]
        payload = web3.toHex(encode(['address', 'address', 'uint256'], [address, address, nft_id]))
    except Exception as e:
        write_to_file('ERRORS.txt', e)
        return None
    lzFee = int(lzEnd_contract.functions.estimateFees(109, holograph_address, '0x', False, '0x').call()[0]*2)
    try:
        transaction = holograph_contract.functions.bridgeOutRequest(
            4,
            nft_address,
            random.randint(450000, 500000),
            400000000000,
            payload
        ).build_transaction({
            'from': address,
            'value': lzFee,
            'gasPrice': web3.toWei(gwei, 'gwei'),
            'nonce': web3.eth.get_transaction_count(address),
            'chainId': 56,
        })
        signed_txn = web3.eth.account.sign_transaction(transaction, private_key)
        tx_hash = web3.toHex(web3.eth.send_raw_transaction(signed_txn.rawTransaction))
        print(f'{Fore.GREEN}[{address}] NFT Bridge SUCCESSFUL; hash: {tx_hash}')
        web3.eth.wait_for_transaction_receipt(tx_hash, timeout=360)
    except Exception as e:
        retry_counter = 1

        print(f'{Fore.RED}[{address}] {e}')
        if str(e) == 'execution reverted: LayerZero: not enough native for fees' and not err:
            if retry_counter <= retries:
                print(f'{Fore.RED}[{time.strftime("%H:%M:%S", time.localtime())}] Retry {retry_counter}/{retries}')
                retry_counter += 1
                bridge(private_key, e)
                time.sleep(failed_delay)
            else:
                print(f'[{address}] NFT Bridge UNSUCCESSFUL')
        else:
            write_to_file('ERRORS.txt', f'{private_key};{e}')
            print(f'[{address}] NFT Bridge UNSUCCESSFUL')
        print(Fore.RESET, end='')
        return None
    print()


def main():
    num_of_accs_today = random.randint(*num_of_accs_per_day)                                                # delays
    delay_between_wallets = (23*60*60 / num_of_accs_today - 3600, 23*60*60 / num_of_accs_today + 3600)      # delays
    print(f'[INFO] Num of acconts today: {num_of_accs_today}')

    privates = read_file('privates.txt')
    for private in privates:
        bridge(private)
        delay = random.randint(*delay_between_wallets)
        while delay > 0:
            unix_time = time.localtime()
            if int(time.strftime("%H", unix_time)) == 0 and int(time.strftime("%M", unix_time)) in (0, 1, 2):
                num_of_accs_today = random.randint(*num_of_accs_per_day)
                print(f'[INFO] Num of acconts today: {num_of_accs_today}')
                delay_between_wallets = (23 * 60 * 60 / num_of_accs_today - 3600, 23 * 60 * 60 / num_of_accs_today + 3600)
            print(f'[INFO] Time to next wallet: {delay}')
            if delay <= 60:
                delay = 0
                time.sleep(delay)
            else:
                delay -= 60
                time.sleep(60)


if __name__ == '__main__':
    main()
